import { useState, useEffect } from "react"

function Answers({questions}){
    const [score,setScore] = useState(0)
    const [i,setI] = useState(0)
    const [showScore,setShowScore] = useState(false)

    useEffect(() => {
        console.log('Do something after counter has changed', i);
     }, [i, showScore]);

    const nextAnswer = (answer) => {
        console.log(i, i)

        if(i < 2){

            setI(i + 1)
        }else{
            console.log(111111)
            // setI(i + 1)
            setShowScore(true)
            console.log(showScore,"showscore")
            return
        }

        if(answer){
            setScore(score + 1)
        }
    }

    return(
        <div>
            {!showScore ?  <>
            <div>{questions[i].question}</div>
            {questions[i].answer.map(item => {
                   return(
                    <p className="answer" onClick={() => nextAnswer(item.isTrue)} key={item.answerText}>
                    {item.answerText}
                    </p>
                    )
                }
            )
            }
            </> : "your score is " + score
                }
        </div> 
    )

}

export default Answers
