import { useState } from "react"

function Answers({questions,i,setI}){
    let [result,setResult] = useState(0)

    function nextAnswer(answer){
        setI(i++)
        if(i > 3){
            console.log(result)
        }else if(answer = true){
            setResult(result ++)
        }
    }

    return(
        <div>
            {questions[i].answer.map(item => {
            return(
                <p className="answer" onClick={nextAnswer(item.isTrue)}>
                {item.answerText}
                </p>
            )
            })
            }
        </div> 
    )

}

export default Answers
