function Answers({questions,i,setI}){

    function nextAnswer(){
        setI(i++)
        if(i > 3){
            alert("result")
        }
    }

    return(
        <div>
            {questions[i].answer.map(item => {
                return(<p className="answer" onClick={nextAnswer}>
                {item.answerText}
            </p>)
            })
            }
        </div> 
    )

}

export default Answers
