function Answers({questions,i,setI}){

    function nextAnswer(){
        setI(i++)
        if(i > 3){
            alert("result")
        }
    }

    return(
        <div>
            <p className="answer" onClick={nextAnswer}>
                {questions[i].answer[0].answerText}
            </p>
            <p className="answer" onClick={nextAnswer}>
                {questions[i].answer[1].answerText}
            </p>
            <p className="answer" onClick={nextAnswer}>
                {questions[i].answer[2].answerText}
            </p>
            <p className="answer" onClick={nextAnswer}>
                {questions[i].answer[3].answerText}
            </p>
        </div> 
    )

}

export default Answers
