import { useState } from "react"

function Answers({questions,e}){
    const [score,setScore] = useState(0)
    const [i,setI] = useState(0)
    const [showScore,setShowScore] = useState(false)

    function nextAnswer(answer){
        if(i <= 2){
            setI(i + 1)
            if(answer){
                setScore(score + 1)
            }
            console.log(score);
        }else{
            setShowScore(true)
        }


    }

    return(
        <div>
            {!showScore ?  <>
            <div>{questions[i].question}</div>
            {questions[i].answer.map(item => {
                   return(
                    <p className="answer" onClick={(e) => nextAnswer(item.isTrue,e)} key={item.answerText}>
                    {item.answerText}
                    </p>
                    )
                }
            )
            }
            </> : "your score is " + score
                }
        </div> 
    )

}

export default Answers
